        /// <summary>
        /// The key for the message similar to "{0} successfully changed.".
        /// </summary>
        public static readonly string SuccessfulAddOrUpdate = "SuccessfulAddOrUpdate";

        /// <summary>
        /// The key for the message similar to "{0} successfully deleted.".
        /// </summary>
        public static readonly string SuccessfulDelete = "SuccessfulDelete";

        /// <summary>
        /// The key for the message similar to "{0} successfully updated.".
        /// </summary>
        public static readonly string SuccessfulUpdate = "SuccessfulUpdate";

